WorkflowProcessEvent.eventDescription:
function afterCancelProcess(colleagueId,processId){
	log.info("================= AFTER CANCEL PROCESS ====================");
	hAPI.setCardValue("status", "cancelado");
	log.info("Cancelando atividade do plano de acao....");
}

function afterProcessCreate(processId){
	log.info("afterProcessCreate (APA)");
	hAPI.setCardValue("status", "ativo");
	hAPI.setCardValue("cdAtividadePA", processId);

}

function afterProcessFinish(processId){
	log.info("================= AFTER PROCESS FINISH ====================");
	hAPI.setCardValue("status", "finalizado");
	log.info("Finalizando atividade do plano de acao....");
}

function afterStateEntry(sequenceId){

	var nrAtividadeAtual = getValue("WKNumState");
    //var THREAD_SEQUENCE = 0;
    //var ULTIMO_SEGUNDO = 86399;

    log.info ("afterStateEntry (APA)");
    log.info ("sequenceId " + sequenceId.toString());
    log.info ("nrAtividadeAtual " + nrAtividadeAtual.toString());

    //Quando vem do fluxo Plano de Ação a atividade é = 1
    if (nrAtividadeAtual == 0 || nrAtividadeAtual == 1) {
        var dataPrazoForm = hAPI.getCardValue("nmPrazoDeConclusao"); //prazo gerado pelo Plano
        log.info("prazo do plano  " + dataPrazoForm);
        log.info(dataPrazoForm == null);
        log.info(dataPrazoForm != '');
        if(dataPrazoForm != '') { alteraPrazo(dataPrazoForm);}        
    }

    //Quando o Gestor altera o prazo a atividade é 12
    if (nrAtividadeAtual == 12) {
    	//novo prazo informado pelo gestor. 
        var dtPrazoAPA    = hAPI.getCardValue("dtPrazoAPA"); 
        log.info("prazo do gestor " + dtPrazoAPA);

        if (dtPrazoAPA != undefined && dtPrazoAPA != null && dtPrazoAPA != '') {
        	//No formulario o campo é tipo date e o formato é AAAA-MM-DD
        	dtPrazoAPA = dtPrazoAPA.substring(8,10) + "/" + dtPrazoAPA.substring(5,7) + "/" + dtPrazoAPA.substring(0,4); 
        	log.info("dtPrazoAPA: " + dtPrazoAPA);

        	alteraPrazo(dtPrazoAPA);
        }
    }

}

function afterTaskComplete(colleagueId,nextSequenceId,userList){

	var nrAtividadeAtual = getValue("WKNumState");
	var matUsuario       = getValue("WKUser");
    var nrSolic          = getValue("WKNumProces");

    log.info ("afterTaskComplete(APA)");
    log.info ("colleagueId     : " + colleagueId);
    log.info ("nextSequenceId  : " + nextSequenceId.toString());
    log.info ("userList        : " + userList);
    log.info ("nrAtividadeAtual: " + nrAtividadeAtual);

	if(nrAtividadeAtual == 2) { //2-Executar Atividade
		hAPI.setTaskComments(matUsuario, nrSolic, 0, hAPI.getCardValue("dsAtividade"));
		//Enviar email para o Responsável do Plano de Ação
	}

	if(nrAtividadeAtual == 12) { //12-Definir Novo Prazo
		hAPI.setTaskComments(matUsuario, nrSolic, 0, hAPI.getCardValue("nmComentarioPrazo"));
	}

}

function alteraPrazo(dataPrazoForm) {
    var THREAD_SEQUENCE = 0;
    var ULTIMO_SEGUNDO = 86399;

	var arrayData = dataPrazoForm.split("/", 3);

    var dataPrazo = java.util.Calendar.getInstance();

    dataPrazo.set(java.util.Calendar.DAY_OF_MONTH, new java.lang.Integer(arrayData[0]));
    dataPrazo.set(java.util.Calendar.MONTH, new java.lang.Integer(arrayData[1]) -1);
    dataPrazo.set(java.util.Calendar.YEAR, new java.lang.Integer(arrayData[2]));

    log.info ("=========================== alteraPrazo(" + dataPrazoForm + ") =============================");
    log.info("dataPrazoForm: " + dataPrazoForm);
    log.info("dataNovoPrazo: " + dataPrazo.getTime());

    var codigoSolicitacao = getValue("WKNumProces");
    var responsavel   = hAPI.getCardValue("hiddenMatResponsavel");

    log.info("codigoSolicitacao   : " + codigoSolicitacao);
    log.info("THREAD_SEQUENCE     : " + THREAD_SEQUENCE);
    log.info("responsavel         : " + responsavel);
    log.info("ULTIMO_SEGUNDO      : " + ULTIMO_SEGUNDO);

    try {
    	hAPI.setDueDate(codigoSolicitacao, THREAD_SEQUENCE, responsavel, dataPrazo.getTime(), ULTIMO_SEGUNDO);
    } catch (e) {
        log.info ("================== alteraPrazo() deu erro ===> " + e);
    }

}

function beforeCancelProcess(colleagueId,processId){
	log.info("================= BEFORE CANCEL PROCESS ====================");
	hAPI.setCardValue("status", "CANCELADO");
	log.info("CANCELADO atividade do plano de acao....");
}

function beforeSendData(customFields,customFacts){

    log.info("################## beforeSendData ##################");
    log.info("############## Atividades Plano de Ação ##############");  

    //Atividades Plano de Ação 
    customFields[0] = hAPI.getCardValue("nrFicha");//Numero da Ocorrência
    customFields[1] = hAPI.getCardValue("cdOcorrencia");//Numero do registro de ocorrência
    customFields[2] = hAPI.getCardValue("cdPlanoAcao");//Numero do Plano de Ação
    customFields[3] = hAPI.getCardValue("nmResponsavelPA");//Responsavel pelo Plano de Ação
    customFields[4] = hAPI.getCardValue("nmOquCom");//O que? Como?
    customFields[5] = hAPI.getCardValue("nmResponsavel");//Responsavel
    customFields[6] = hAPI.getCardValue("nmLiderdoResp");//Líder Responsável 
    customFields[7] = hAPI.getCardValue("nmPrazoDeConclusao");//Prazo de Conclusão
    customFields[8] = hAPI.getCardValue("dsAtividade");//Descrição da Atividade
    customFields[9] = hAPI.getCardValue("dtPrazoAPA");//Novo Prazo - Gestor
    customFields[10] = hAPI.getCardValue("nmComentarioPrazo");//Comentário Novo Prazo


}

function needEffectiveness(){
	var checked = hAPI.getCardValue("fgVerifEficacia")=="on";
	log.info("================ PRECISA ANALISE DE EFICACIA?  " + checked + " ==================");
	return checked;
}
